import React, { useState } from 'react';
import { v4 as uuidv4 } from 'uuid';
import { DragDropContext, Droppable, Draggable } from 'react-beautiful-dnd';

const GroceryList = () => {
  const [groceryList, setGroceryList] = useState([]);
  const groceryItems = groceryList.map((item, index) => (
    // dnd library will let us drag & drop stuff inside "Draggable" component
    <Draggable key={item.id} draggableId={item.id} index={index}>
      {(provided) => (
        <li
          ref={provided.innerRef}
          {...provided.draggableProps}
          {...provided.dragHandleProps}
        >
          <input value={item.name} />
          <button onClick={() => removeGroceryItem(item.id)}>Remove</button>
        </li>
      )}
    </Draggable>
  ));

  const addGroceryItem = () => {
    setGroceryList([...groceryList, { id: uuidv4(), name: '' }]);
  };

  const removeGroceryItem = (id) => {
    setGroceryList(groceryList.filter((item) => item.id !== id));
  };

  const handleDragEnd = (result) => {
    if (!result.destination) return; // Item was dropped outside of the list

    const items = Array.from(groceryList);
    const [reorderedItem] = items.splice(result.source.index, 1);
    items.splice(result.destination.index, 0, reorderedItem);

    setGroceryList(items);
  };

  return (
    <div>
      <DragDropContext onDragEnd={handleDragEnd}>
        <Droppable droppableId="groceryList">
          {(provided) => (
            <ul {...provided.droppableProps} ref={provided.innerRef}>
              {groceryItems}
              {provided.placeholder}
            </ul>
          )}
        </Droppable>
      </DragDropContext>
      <button onClick={addGroceryItem}>Add Item</button>
    </div>
  );
};

export default GroceryList;
