import React, { useState } from 'react';
// uuid's v4 algorithm for generating random ID for each grocery item
import { v4 as uuidv4 } from 'uuid';

const GroceryList = () => {
  const [groceryList, setGroceryList] = useState([]);
  const groceryItems = groceryList.map(item => (
    <li key={item.id}>
      {item.name}
      <button onClick={() => renameGroceryItem(item.id, "OOF")}>Rename</button>
      <button onClick={() => removeGroceryItem(item.id)}>Remove</button>
    </li>
  ));

  const addGroceryItem = (name) => {
    setGroceryList([...groceryList, { id: uuidv4(), name }]);
  };

  const renameGroceryItem = (id, name) => {
    setGroceryList(groceryList.map(item => 
        item.id === id ? {...item, name} : item
    ));
  };

  const removeGroceryItem = (id) => {
    setGroceryList(groceryList.filter(item => item.id !== id));
  };

  return (
    <div>
        <ul>{groceryItems}</ul>
        <button onClick={() => addGroceryItem("oof")}>
            Add Item
        </button>
    </div>
  );
};

export default GroceryList;
