import React, { useState } from 'react';
// uuid's v4 algorithm for generating random ID for each grocery item
import { v4 as uuidv4 } from 'uuid';

const GroceryList = () => {
  const [groceryList, setGroceryList] = useState([]);
  const groceryItems = groceryList.map(item => (
    <li key={item.id}>
      {item.name}
      <button onClick={() => removeGroceryItem(item.id)}>Remove</button>
    </li>
  ));

  const addGroceryItem = (name) => {
    setGroceryList([...groceryList, { id: uuidv4(), name: item }]);
  };

  const renameGroceryItem = (name)

  const removeGroceryItem = (id) => {
    setGroceryList(groceryList.filter(item => item.id !== id));
  };

  return (
    <div>
      <ul>{groceryItems}</ul>
      <input
        type="text"
        placeholder="Add item..."
        onKeyPress={(e) => {
          if (e.key === 'Enter') {
            addGroceryItem(e.target.value);
            e.target.value = '';
          }
        }}
      />
    </div>
  );
};

export default GroceryList;
